import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [loding, setLoading] = useState(true);
  const [conis, setCoins] = useState([]);
  const [amount, setAmount] = useState(0);
  const [price, setSelectedCoin] = useState({});

  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers")
      .then((response) => response.json())
      .then((json) => {
        setCoins(json);
        setLoading(false);
      });
  }, []);

  useEffect(() => {
    console.log(amount);
  }, [amount]);

  const onChange = (e) => {
    const selectItem = e.target.value;

    console.dir(e);
    console.log(selectItem, e.target.selectedIndex);
  };

  return (
    <div className="App">
      <h1>The Coins! ({conis.length})</h1>
      {loding ? (
        <strong>Loading...</strong>
      ) : (
        <div>
          <h3>Choose the coin you want to purchase.</h3>
          <select onChange={onChange}>
            {conis.map((coin) => (
              <option key={coin.id}>
                {coin.name}({coin.symbol} :{" "}
                {Number(coin.quotes.USD.price).toFixed(2)} USD)
              </option>
            ))}
          </select>
          <h3>How much do you have?</h3>
          <input
            type="number"
            placeholder="USD"
            value={amount}
            onChange={(e) => {
              setAmount(e.target.value);
            }}
          />

          <h4>
            You can buy {} {}.
          </h4>
        </div>
      )}
    </div>
  );
}

export default App;
